        senha: null
      },
      rules: {
        required: value => !!value || "ObrigatÃ³rio!"
      },
      loginErrors: []
    };
  },
  methods: {
    realizarLogin() {
      this.loginErrors = []
      this.$axios
        .post("/signin", this.formData)
        .then(res => {
          this.$store.dispatch('login/userLogin', {
            loginData: res.data.acessoToken,
            router: this.$router
          })
        })
        .catch(err => {
          console.log(err)
          this.loginErrors = [err.response.data]
        });
    }
  }
};
